mapper.select:
SELECT
-- 级联表的字段 ,kecheng.kecheng_uuid_number as kechengUuidNumber ,kecheng.kecheng_name as kechengName ,kecheng.kecheng_types as kechengTypes ,kecheng.kecheng_file as kechengFile ,kecheng.kecheng_content as kechengContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_types as yonghuTypes ,yonghu.yonghu_phone as yonghuPhone ,yonghu.shengri_time as shengriTime ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM chengji a left JOIN kecheng kecheng ON a.kecheng_id = kecheng.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.kecheng_id = #{params.kechengId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.chengji_number >= #{params.chengjiNumberStart}
and a.chengji_number <= #{params.chengjiNumberEnd}
and a.chengji_number = #{params.chengjiNumber}
and a.chengji_content like CONCAT('%',#{params.chengjiContent},'%')
and a.kecheng_id IS NOT NULL
and kecheng.kecheng_uuid_number like CONCAT('%',#{params.kechengUuidNumber},'%')
and kecheng.kecheng_name like CONCAT('%',#{params.kechengName},'%')
and kecheng.kecheng_types = #{params.kechengTypes}
and kecheng.kecheng_content like CONCAT('%',#{params.kechengContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_types = #{params.yonghuTypes}
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and UNIX_TIMESTAMP(yonghu.shengri_time) >= UNIX_TIMESTAMP(#{params.shengriTimeStart})
and UNIX_TIMESTAMP(yonghu.shengri_time) <= UNIX_TIMESTAMP(#{params.shengriTimeEnd})
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
